import React, { useState } from 'react';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();
    alert(`Logged in with ${email}`);
  };

  return (
    <div className="max-w-md mx-auto mt-8 p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-2xl font-semibold mb-4">Login</h2>
      <form onSubmit={handleLogin} className="max-w-md mx-auto mt-10 bg-white p-8 rounded-xl shadow-lg space-y-4">
  <h2 className="text-2xl font-bold text-gray-800">Login</h2>
  <input
    type="email"
    placeholder="Email"
    value={email}
    onChange={(e) => setEmail(e.target.value)}
    className="w-full p-3 border rounded focus:ring-2 focus:ring-blue-400"
  />
  <input
    type="password"
    placeholder="Password"
    value={password}
    onChange={(e) => setPassword(e.target.value)}
    className="w-full p-3 border rounded focus:ring-2 focus:ring-blue-400"
  />
  <button
    type="submit"
    className="w-full py-3 bg-gradient-to-r from-green-400 to-blue-500 text-white rounded-lg hover:scale-105 transition"
  >
    Login
  </button>
</form>

    </div>
  );
}

export default Login;
