import React from 'react';

function Jobs() {
  return (
    <div className="max-w-6xl mx-auto mt-10">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">Latest Jobs</h2>
      <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {[
          { title: "Frontend Developer", company: "Google", location: "Bangalore" },
          { title: "Backend Developer", company: "Microsoft", location: "Hyderabad" },
          { title: "Full Stack Engineer", company: "Amazon", location: "Pune" }
        ].map((job, i) => (
          <div
            key={i}
            className="p-6 bg-white rounded-xl shadow-md hover:shadow-2xl transition duration-300 transform hover:scale-105"
          >
            <h3 className="text-xl font-semibold">{job.title}</h3>
            <p className="text-gray-500">{job.company} â€“ {job.location}</p>
            <button className="mt-4 px-4 py-2 bg-gradient-to-r from-green-400 to-blue-500 text-white rounded-lg shadow hover:scale-105 transition">
              Apply Now
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Jobs;   // ðŸ‘ˆ VERY IMPORTANT
