import React, { useState } from 'react';

function PostJob() {
  const [title, setTitle] = useState('');
  const [company, setCompany] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Job Posted: ${title} at ${company}`);
  };

  return (
    <div className="max-w-md mx-auto mt-8 p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-2xl font-semibold mb-4">Post a Job</h2>
      <form onSubmit={handleSubmit} className="max-w-lg mx-auto mt-10 bg-white p-8 rounded-xl shadow-lg space-y-4">
  <h2 className="text-2xl font-bold text-gray-800">Post a Job</h2>
  <input
    type="text"
    placeholder="Job Title"
    value={title}
    onChange={(e) => setTitle(e.target.value)}
    className="w-full p-3 border rounded focus:ring-2 focus:ring-blue-400"
  />
  <input
    type="text"
    placeholder="Company"
    value={company}
    onChange={(e) => setCompany(e.target.value)}
    className="w-full p-3 border rounded focus:ring-2 focus:ring-blue-400"
  />
  <button
    type="submit"
    className="w-full py-3 bg-gradient-to-r from-blue-500 to-indigo-600 text-white rounded-lg hover:scale-105 transition"
  >
    Post Job
  </button>
</form>

    </div>
  );
}

export default PostJob;
