import React, { useState } from 'react';
import { registerUser } from '../services/api';

function Register() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [mobile, setMobile] = useState('');

  const handleRegister = (e) => {
    e.preventDefault();
    registerUser({ email, password, mobile })
      .then(res => alert('Registration successful'))
      .catch(err => alert('Email already exists'));
  };

  return (
    <div className="max-w-md mx-auto mt-8 p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-4">Register</h2>
      <form onSubmit={handleRegister} className="space-y-4">
        <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} className="w-full p-3 border rounded" />
        <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} className="w-full p-3 border rounded" />
        <input type="text" placeholder="Mobile Number" value={mobile} onChange={e => setMobile(e.target.value)} className="w-full p-3 border rounded" />
        <button type="submit" className="w-full py-3 bg-gradient-to-r from-green-400 to-blue-500 text-white rounded-lg">Register</button>
      </form>
    </div>
  );
}

export default Register;
