import React, { useState } from 'react';
import { postJob } from '../services/api';

function PostJob() {
  const [title, setTitle] = useState('');
  const [company, setCompany] = useState('');
  const [location, setLocation] = useState('');
  const [salary, setSalary] = useState('');
  const [description, setDescription] = useState('');
  const [requirements, setRequirements] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    postJob({ title, company, location, salary, description, requirements, employerId: 1 })
      .then(res => alert('Job posted successfully'))
      .catch(err => alert('Error posting job'));
  };

  return (
    <div className="max-w-lg mx-auto mt-8 p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-4">Post a Job</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input type="text" placeholder="Job Title" value={title} onChange={e => setTitle(e.target.value)} className="w-full p-3 border rounded" />
        <input type="text" placeholder="Company" value={company} onChange={e => setCompany(e.target.value)} className="w-full p-3 border rounded" />
        <input type="text" placeholder="Location" value={location} onChange={e => setLocation(e.target.value)} className="w-full p-3 border rounded" />
        <input type="text" placeholder="Salary" value={salary} onChange={e => setSalary(e.target.value)} className="w-full p-3 border rounded" />
        <textarea placeholder="Description" value={description} onChange={e => setDescription(e.target.value)} className="w-full p-3 border rounded"></textarea>
        <textarea placeholder="Requirements" value={requirements} onChange={e => setRequirements(e.target.value)} className="w-full p-3 border rounded"></textarea>
        <button type="submit" className="w-full py-3 bg-gradient-to-r from-blue-500 to-indigo-600 text-white rounded-lg">Post Job</button>
      </form>
    </div>
  );
}

export default PostJob;
